package loadbalancer

type RoundRobin struct {
    services []*Service
    current  uint64
    mu       sync.RWMutex
}

func (rb *RoundRobin) Next() *Service {
    rb.mu.Lock()
    defer rb.mu.Unlock()

    if len(rb.services) == 0 {
        return nil
    }

    service := rb.services[rb.current%uint64(len(rb.services))]
    rb.current++

    return service
}
